<?php

/**
 * Effect
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 *
 * @package    cosmedecorte
 * @subpackage model
 * @author     BROADTECH INC.
 * @version    SVN: $Id: Effect.class.php 1194 2012-10-26 07:08:28Z oda $
 */
class Effect extends BaseEffect
{
    /**
     * 半角文字を使用した効果名を返却する。
     *
     * @return string 半角文字を使用した効果名
     */
    public function getHName()
    {
        return Util::convertStringIntoHalfWidth($this->getName());
    }

    /**
     * 効果画像ファイルの絶対パスを取得する。
     */
    public function getAbsoluteImagePath()
    {
        return sfConfig::get('sf_web_dir') .
            '/images/' . $this->getRelativeImagePath();
    }

    /**
     * 効果画像ファイルの相対パス(images からの相対パス)を取得する。
     */
    public function getRelativeImagePath()
    {
        return 'effect/' . $this->getSlug() . '.jpg';
    }

    /**
     * オブジェクトの現在の内容を本番環境に反映させる。
     */
    public function liveup()
    {
        $category = $this->getCategory();

        Doctrine_Manager::getInstance()->setCurrentConnection('livedb');

        $live_effect =
            EffectTable::getInstance()->findOneBySlug($this->getSlug());

        $live_category =
            CategoryTable::getInstance()->findOneBySlug($category->getSlug());
        if (!$live_category) {
            Doctrine_Manager::getInstance()->setCurrentConnection('doctrine');

            throw new NoCategoryException();
        }

        if ($live_effect) {
            $values = $this->toArray();
            unset($values['id']);
            unset($values['created_at']);
            unset($values['updated_at']);
            $live_effect->fromArray($values);
            $live_effect->setUpdatedAt(null);
        } else {
            $live_effect = $this->copy();
            $live_effect->setCreatedAt(null);
            $live_effect->setUpdatedAt(null);
        }
        $live_effect->setCategory($live_category);
        $live_effect->
            save(Doctrine_Manager::getInstance()->getConnection('livedb'));

        Doctrine_Manager::getInstance()->setCurrentConnection('doctrine');
    }
}
