class FaqContent extends BaseFaqContent
{
    public function getDetailsQuery()
    {
        return FaqDetailTable::getInstance()->createQuery('d')->
            innerJoin('d.FaqContentDetail cd')->
            innerJoin('cd.FaqContent c')->
            where('c.id = ' . $this->getId())->orderBy('d.priority');
    }

    public function getHTitle1()
    {
        return Util::convertStringIntoHalfWidth(parent::_get('title1'));
    }

    public function getHTitle2()
    {
        return Util::convertStringIntoHalfWidth(parent::_get('title2'));
    }

    public function getHTitle3()
    {
        return Util::convertStringIntoHalfWidth(parent::_get('title3'));
    }

    public function __toString()
    {
        return $this->getTitle1();
    }
}
